class Kla_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get kla by id
     */
    function get_kla($id)
    {
        return $this->db->get_where('Klas',array('id'=>$id))->row_array();
    }
        
    /*
     * Get all klas
     */
    function get_all_klas()
    {
        $this->db->order_by('id', 'desc');
        return $this->db->get('Klas')->result_array();
    }
        
