     */
    function update_kla($id,$params)
    {
        $this->db->where('id',$id);
        return $this->db->update('Klas',$params);
    }
    
    /*
     * function to delete kla
     */
    function delete_kla($id)
    {
        return $this->db->delete('Klas',array('id'=>$id));
    }
